mapper.select:
select ID,NOTICE_TYPE,USER_GROUP,USABLE_STATUS,CREATE_TIME,TITLE,BRIEF_CONTENT from app_notice_info where 1=1
and NOTICE_TYPE = #{queryBean.noticeType,jdbcType=VARCHAR}
and USER_GROUP = #{queryBean.userGroup,jdbcType=VARCHAR}
and USABLE_STATUS = 'ENABLE' order by CREATE_TIME desc

select ID,TITLE,CONTENT,CREATE_TIME
from app_notice_info
where ID = #{noticeId,jdbcType=VARCHAR}
