mapper.select:
SELECT user_no,DATE_FORMAT(`month`,'%Y-%m-%d') as 'month1',sum(total_amount) as amount
FROM reward_gather_month
GROUP  BY user_no,`month`
HAVING user_no = #{userNo,jdbcType=VARCHAR}

  AND `month` >= #{startDate,jdbcType=DATE}


  AND `month` <= #{endDate,jdbcType=DATE}

ORDER  BY `month` DESC

SELECT user_no,DATE_FORMAT(`month`,'%Y-%m-%d') as 'month1',sum(total_amount) as amount
FROM reward_gather_month
GROUP  BY user_no,`month`,parent_business_code
HAVING user_no = #{userNo,jdbcType=VARCHAR}
AND parent_business_code = #{parentBusinessCode,jdbcType=VARCHAR}

  AND `month` >= #{startDate,jdbcType=DATE}


  AND `month` <= #{endDate,jdbcType=DATE}

ORDER  BY `month` DESC

SELECT 
FROM reward_gather_month
WHERE 1=1

  AND user_no = #{userNo,jdbcType=VARCHAR}

AND month = #{month,jdbcType=DATE}
AND business_code = #{businessCode,jdbcType=VARCHAR}
