mapper.update:
	update menu_role_link
  set 
	  STATUS = #{status,jdbcType=VARCHAR}
  where MENU_ID = #{menuId,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId ,jdbcType=VARCHAR}

mapper.select:
select 
menu_id 
from 
menu_role_link mrl
where mrl.menu_id = #{menuId,jdbcType=VARCHAR}
and ROLE_ID =  #{roleId,jdbcType=VARCHAR}
